import React from 'react';
import { Header } from '../header/Header';
import { Footer } from '../footer/Footer';

function ViewBills(props) {
    return (
        <>
        <Header/>
        <div  className="min-vh-100 " style={{
      backgroundImage: `url("/bg_img.svg")`, 
      backgroundSize: 'cover',             
      backgroundPosition: 'center',        
  }}>
      <div className="container min-vh-100 " >
  <h2 style={{paddingTop:50}}>Pending Bills</h2>
  <p>All the Pending bills are as follows. <br/><small><strong>**Note: Click on row to view more details</strong></small></p>  
  </div>

 {
    billData.map((doc,index)=>{
        return(
        <ShowBills
        bill_id={doc.bill_id}
        description={doc.description}
        amount={doc.amount}
        bill_date={doc.bill_date}
        deadline={doc.deadline}
        remainingAmount={doc.remaining}
        paid={doc.paid}
        key={index}
        index={index}
        parts={doc.parts}
        />
    )
    })

 }
  </div>
  <Footer/>

  </>
    );
}

export default ViewBills;


const ShowBills = (props) => {
    return (
        <>
        <tr   data-bs-toggle="collapse" data-bs-target={"#row"+props.index} aria-expanded="false"  style={{cursor: 'pointer'}}>
        <td><b>{props.pid}</b></td>
        <td>&#8377; {props.amount}/-</td>
        <td>{props.date}</td>
        <td><span style={{color:'#0d6efd'}}><u>More</u></span></td>
      </tr>
      <tr id={'row'+props.index} class="collapse">
      <td colspan="4">
            <table class="table">
              <tr>
                <td>Bill ID</td>
                <td>:&nbsp;&nbsp; {props.bill_id}</td>
              </tr>
              <tr>
                <td>Amount</td>
                <td>:&nbsp;&nbsp; &#8377; {props.amount}/-</td>
              </tr>
              <tr>
                <td>Due Date</td>
                <td>:&nbsp;&nbsp; {props.dueDate}</td>
              </tr>
              <tr>
                <td>Bill Date</td>
                <td>:&nbsp;&nbsp; {props.billDate}</td>
              </tr>

              <tr>
                <td>Remaining Amount</td>
                <td>:&nbsp;&nbsp;  &#8377; {props.remainingAmount}/-</td>
              </tr>
              <tr>
                <td>Remaining Amount</td>
                <td>:&nbsp;&nbsp;  &#8377; {props.paidAmount}/-</td>
              </tr>
              <tr>
                <td>Description</td>
                <td>:&nbsp;&nbsp; {props.description}</td>
              </tr>
            </table>
          </td>
      </tr>



      </>
    )
    }
const PaidBillParts= (props) => {
return (
    <>
    <tr   data-bs-toggle="collapse" data-bs-target={"#row"+props.index} aria-expanded="false"  style={{cursor: 'pointer'}}>
    <td><b>{props.pid}</b></td>
    <td>&#8377; {props.amount}/-</td>
    <td>{props.date}</td>
    <td><span style={{color:'#0d6efd'}}><u>More</u></span></td>
  </tr>
  <tr id={'row'+props.index} class="collapse">
  <td colspan="4">
        <table class="table">
          <tr>
            <td>Payment ID</td>
            <td>:&nbsp;&nbsp; {props.pid}</td>
          </tr>
          <tr>
            <td>Amount</td>
            <td>:&nbsp;&nbsp; &#8377; {props.amount}/-</td>
          </tr>
          <tr>
            <td>Payment Date</td>
            <td>:&nbsp;&nbsp; {props.date}</td>
          </tr>
          <tr>
            <td>Description</td>
            <td>:&nbsp;&nbsp; {props.description}</td>
          </tr>
        </table>
      </td>
  </tr>
  </>
)
}


const billData = [
    {
      bill_id: 'B001',
      description: 'Monthly subscription fee',
      amount: 5000,
      bill_date: '01/11/24',
      deadline: '15/11/24',
      remaining: 2000,
      paid: 3000,
      parts: [
        { paymentId: 'P001', amount: 1500, paymentDate: '05/11/24', description: 'First installment' },
        { paymentId: 'P002', amount: 1500, paymentDate: '10/11/24', description: 'Second installment' }
      ]
    },
    {
      bill_id: 'B002',
      description: 'Office supplies purchase',
      amount: 12000,
