import React,{useState, useEffect } from 'react';
import { Header } from '../header/Header';
import { Footer } from '../footer/Footer';

function ViewBills(props) {
  const [unpaidBills, setUnpaidBills] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Function to fetch unpaid bills from the backend
    const fetchUnpaidBills = async () => {
        try {
          const response = await fetch('http://localhost:8080/api/v1/customers/unpaidbills', {
            method: 'POST', // Change from GET to POST
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                studentId: 1
            })
        });

            if (!response.ok) {
                throw new Error('Failed to fetch unpaid bills');
            }

            const data = await response.json();
            setUnpaidBills(data);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    fetchUnpaidBills();
}, []);
if (loading) {
  return <div>Loading...</div>;
}

if (error) {
  return <div>Error: {error}</div>;
}
    return (
        <>
        <Header/>
        <div  className="min-vh-100 " style={{
      backgroundImage: `url("/bg_img.svg")`, 
      backgroundSize: 'cover',             
