import { Navigate, Outlet } from 'react-router-dom';
import { useEffect, useState } from 'react';

export const RequestAuth = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(null); // null = loading state
    const token = localStorage.getItem('authToken'); // Retrieve JWT from localStorage

    useEffect(() => {
        const validateToken = async () => {
            try {
                const response = await fetch('http://localhost:8080/api/auth/checkValidation', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`, // Include JWT in the Authorization header
                    },
                });
                console.log(response.status)
                if (response.ok) {
                    setIsAuthenticated(true); // Assume the server responds with { valid: true/false }
                } else {
                    setIsAuthenticated(false);
                }
            } catch (error) {
                console.error('Token validation failed:', error);
                setIsAuthenticated(false);
            }
        };

        if (token) {
            validateToken();
        } else {
            setIsAuthenticated(false); // No token found
        }
    }, [token]);

    // Show loading indicator while validating the token
    if (isAuthenticated === null) {
        return <div>Loading...</div>;
    }

    // Redirect to login if not authenticated
    return isAuthenticated ? <Outlet /> : <Navigate to="/login" />;
};
